import React, { useEffect, useState } from 'react';
import { io } from 'socket.io-client';

// Initialize socket with autoConnect: false
const socket = io('http://localhost:5050', { autoConnect: false });

function App() {
  const [connected, setConnected] = useState(false);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  useEffect(() => {
    socket.connect(); // Connect once

    // Event handlers
    const handlePartnerFound = () => {
      setConnected(true);
      setMessages([{ from: 'system', text: 'You are now connected to a stranger.' }]);
    };

    const handleMessage = (msg) => {
      setMessages((prev) => [...prev, { from: 'stranger', text: msg }]);
    };

    const handlePartnerDisconnected = () => {
      setConnected(false);
      setMessages((prev) => [...prev, { from: 'system', text: 'Stranger disconnected. Refresh to reconnect.' }]);
    };

    // Attach listeners
    socket.on('partner-found', handlePartnerFound);
    socket.on('message', handleMessage);
    socket.on('partner-disconnected', handlePartnerDisconnected);

    // Cleanup on unmount
    return () => {
      socket.off('partner-found', handlePartnerFound);
      socket.off('message', handleMessage);
      socket.off('partner-disconnected', handlePartnerDisconnected);
      socket.disconnect();
    };
  }, []); // ðŸ‘ˆ empty array = only once when component mounts

  const sendMessage = () => {
    if (input.trim()) {
      socket.emit('message', input);
      setMessages((prev) => [...prev, { from: 'you', text: input }]);
      setInput('');
    }
  };

  return (
    <div style={{ padding: 20, fontFamily: 'Arial' }}>
      <h1>ðŸŸ¢ Omegle Clone - Text Chat</h1>
      <div style={{
        border: '1px solid gray',
        padding: 10,
        height: 300,
        overflowY: 'scroll',
        marginBottom: 10,
        background: '#f9f9f9'
      }}>
        {messages.map((msg, idx) => (
          <div key={idx}>
            <strong>{msg.from}:</strong> {msg.text}
          </div>
        ))}
      </div>

      {connected ? (
        <>
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
            placeholder="Type a message"
            style={{ width: '80%', padding: 10 }}
          />
          <button onClick={sendMessage} style={{ padding: 10, marginLeft: 10 }}>
            Send
          </button>
        </>
      ) : (
        <p>ðŸ”„ Searching for a partner...</p>
      )}
    </div>
  );
}

export default App;
